import React, { useState } from "react";
import { Link } from 'react-router-dom'; // Import Link from react-router-dom


const Nav = () => {
  
  const [linkState, setLinkState] = useState('home');
  const [search, activateSearch] = useState(false);

  const handleLinkState = (link) => {
    setLinkState(link);
  };

  const handleSearch = () => {
    activateSearch(!search);
  };

  return (  
    <div className="nav">
      <div className="navC">
        <div className="titre">
          <Link to="/">          <img id="Logo" src="./logo.png" alt="Description of the image" /> 
</Link>
        </div>
        <ul className="lien">
        <li className={linkState === 'home' ? 'active' : ''}><Link to="/" onClick={() => { handleLinkState("home"); }}>Accueil</Link></li>
          <li className={linkState === 'product' ? 'active' : ''}><Link to="/products" onClick={() => {handleLinkState('product');}}>Produit</Link></li>
          <li className={linkState === 'about' ? 'active' : ''}><Link to="/about" onClick={() => handleLinkState('about')}>Ã€ propos</Link></li>
          <li  className={linkState === 'contact' ? 'active' : ''}><a href="mailto:Bioplexbioplex@gmail.com">Contact</a></li>
        </ul>
        <div className="search">
          <button className="button" onClick={handleSearch}>
            <img src="./search.png" alt="Button Image" />
          </button>
        </div>
      </div>
      <div className="searchInput"> 
        <input type="text" className={search ? 'activeIN' : 'closeIN'} />
      </div>
    </div>
  );
}

export default Nav;
