import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios for making HTTP requests

const cities = [
  'Algiers',
  'Oran',
  'Constantine',
  'Annaba',
  // Add more cities here...
];

const Forme = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [city, setCity] = useState('');
  const [productInfo, setProductInfo] = useState(null); // Initialize productInfo as null

  // Empty dependency array ensures this effect runs only once on component mount

  const handleSubmit = async (e) => { 
    e.preventDefault();
    try {
      useEffect(() => {
        // Fetch product info from local storage when component mounts
        const storedProductInfo = localStorage.getItem('productInfo');
        if (storedProductInfo) {
          setProductInfo(JSON.parse(storedProductInfo));
        }
      }, []); 
      const formData = { name, email, city}; 
      console.log(formData)
      const response = await axios.post('http://127.0.0.1:8000/submit-form/',formData,productInfo);
      console.log('Form submitted successfully:', response.data);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="city">City:</label>
        <select
          id="city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        >
          <option value="">Select a city</option>
          {cities.map((city) => (
            <option key={city} value={city}>
              {city}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Forme;
