import React,{ useState } from 'react';
import { Link } from 'react-router-dom';
function Footer({ scrollToSection }) {

    const [linkState, setLinkState] = useState('home');
    const handleLinkState = (link) => {
        setLinkState(link);
        scrollToSection(link); 
      };
  return (
    <div className='footer'>
      <div className='firstleft'>
        <h1>BEAUTYPLEX</h1>
        <br/>
        <h4>
        Découvrez une sélection soigneusement choisie <br/> 
        pour sublimer votre beauté naturelle.
        </h4>

      </div>

      <ul >
      <li className={linkState === 'home' ? 'active' : ''}><Link to="/" onClick={() => { handleLinkState("home"); }}>Accueil</Link></li>
          <li className={linkState === 'product' ? 'active' : ''}><Link to="/products" onClick={() => {handleLinkState('product');}}>Produit</Link></li>
          <li className={linkState === 'about' ? 'active' : ''}><Link to="/about" onClick={() => handleLinkState('about')}>À propos</Link></li>
          <li ><a href="mailto:Bioplexbioplex@gmail.com">Contact</a></li>

        </ul>




      <p>© BEAUTYPLEX. All Rights Reserved 2024</p>
    </div>

  );
}

export default Footer;
