import React, { useState } from "react";
import { Link } from "react-router-dom";

const Nav = () => {
  const [linkState, setLinkState] = useState('home');
  const [search, activateSearch] = useState(false);

  const handleLinkState = (link) => {
    setLinkState(link);
    scrollToSection(link); // Scroll to the corresponding section
  };

  const handleSearch = () => {
    activateSearch(!search);
  };

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (  
    <div className="nav">
      <div className="navC">
        <div className="titre">
          <img id="Logo" src="./logo.png" alt="Description of the image" /> 
        </div>
        <ul className="lien">
          <li className={linkState === 'home' ? 'active' : ''}><Link to="/" onClick={() => handleLinkState('home')}>Home</Link></li>
          <li className={linkState === 'about' ? 'active' : ''}><Link to="/about" onClick={() => handleLinkState('about')}>About</Link></li>
          <li className={linkState === 'product' ? 'active' : ''}><Link to="/products" onClick={() => handleLinkState('product')}>Notre Produits</Link></li>
          <li className={linkState === 'contact' ? 'active' : ''}><Link to="/contact" onClick={() => handleLinkState('contact')}>Contact</Link></li>
        </ul>
        <div className="search">
          <button className="button" onClick={handleSearch}>
            <img src="./search.png" alt="Button Image" />
          </button>
        </div>
      </div>
      <div className="searchInput"> 
        <input type="text" className={search ? 'activeIN' : 'closeIN'} />
      </div>
    </div>
  );
}

export default Nav;
