import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios for making HTTP requests

const cities = [
  'Algiers',
  'Oran',
  'Constantine',
  'Annaba',
  // Add more cities here...
];

const Forme = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [productInfo, setProductInfo] = useState(null); // State to store product information

  useEffect(() => {
    // Fetch product information from the localhost when the component mounts
    fetchProductInfo();
  }, []);

  const fetchProductInfo = async () => {
    try {
      // Fetch product information from the localhost
      const response = await axios.get('http://localhost:8000/get-product-info');
      setProductInfo(response.data); // Store product information in state
    } catch (error) {
      console.error('Error fetching product info:', error);
    }
  };

  const handleSubmit = async (e) => { // Use async function for asynchronous requests
    e.preventDefault();
    try {
      const formData = { name, email, selectedCity, productInfo }; // Include product info in the form data
      const response = await axios.post('http://localhost:8000/submit-form/', formData); // Change URL to match your FastAPI server
      console.log('Form submitted successfully:', response.data);
      // You can add your logic here to handle successful form submission
    } catch (error) {
      console.error('Error submitting form:', error);
      // You can add your logic here to handle errors
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="city">City:</label>
        <select
          id="city"
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
          required
        >
          <option value="">Select a city</option>
          {cities.map((city) => (
            <option key={city} value={city}>
              {city}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Forme;
